import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

class Counter extends Component {
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"],
  };

  render() {
    let badgeColor = this.getBadgeColor();

    return (
      <div>
        // dynamically rendering class
        <span className={this.getBadgeColor()}>{this.formatCount()}</span>

        <button className="btn btn-secondary">Increment</button>

        // dynamically rendering lists
        <ul>
          {this.state.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }

  getBadgeColor() {
    let badgeColor = "badge m-2 bg-";
    badgeColor += this.state.count == 0 ? "warning" : "primary";
    return badgeColor;
  }

  formatCount() {
    const { count } = this.state;

    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
